import React, { useEffect, useState } from 'react';
import Sidenav from '../Widget/Stocklist/Sidenav/SideNav';
import {getUser} from "../utils/Common";
import "../DashBoard/Dashboard.css"
function Dashboard(){

    const [overall, setOverall]=useState([]);
    const [totalUnits,setTotalUnits]=useState([]);
    const [inverstment, setInverstments]=useState([]);
    const [soldAmount,setSoldAmount]=useState([]);
    const [currentAmount, setCurrentAmount]=useState([]);
    const [totalProfit, setTotalProfit]=useState([]);
    const user=getUser();

    const [error, setError]=useState(null);

    const [stocks,setStocks]=useState([]);


    const [showForm, setShowForm]=useState(false);
    const showForms=()=>{

        setShowForm(!showForm);
    }




    useEffect(()=>{

        fetch(`ttp://localhost:8000/get-all?user=${user}`)
        .then(res=>res.json())
        .then(
            (result)=>{

                setStocks(result);
            },
            (error)=>{
                setError(error);


            }
        )
    },[])
        return (

            <>
    <div className="homepage-main">
    <div className="homepage-sidenav">
    <Sidenav/>

    </div>

        <div className="homepage-part1">

         Hello    {user}
        </div>

        <div className="homepage-part2">

        <table>

    <tr>

        <th>Stock Name</th>
        <th>Transaction Type</th>
        <th>quantity</th>
        <th>
            Amount
        </th>
        <th>Transaction Date</th>
    </tr>
    {
    stocks.map(item=>(

       <tr>

           <td>

               {item.stockname}
           </td>
           <td>
             {item.transactiontype}  
           </td>
    <td>


    {item.quantity}
    </td>

    <td>
    {item.amount}

    </td>

    <td>
    {item.transactiondate}
    </td>

       </tr>
    ))
    }
    </table>
        </div>


    <div className="add-button">
    <button className="add-stock-mockup" onClick={showForms} > Add Stock Mockup</button>
    {

        showForm && (
            // <Stocklist/>
           <StockForm/>
        )
    }
    </div>

    </div>

            </>
        );
}

export default Dashboard;
