import React, { Component } from 'react';
import Select from "react-select";
import axios from "axios";

class AddStockMockup extends Component{


    constructor(props){

        super(props);
        this.state={

            selectOptions:[],
           stock_name:""
        }



    }


    async getOptions(){

        const res=await axios.get("http://localhost:8000/all-stocks")
        const data=res.data

        const options=data.map(d=>(

            {

                "stockname":d.stockname
            }
        ))

        this.setState({selectOptions:options})
    }

    handleChange(e){
        this.setState({stock_name:e.stockname})
    }


    componentDidMount(){

        this.getOptions();
    }



render(){
   console.log(this.state.selectOptions)
   const customStyles = {
    option: (provided, state) => ({
      ...provided,
      borderBottom: '1px dotted pink',
      color: state.isSelected ? 'red' : 'blue',
      padding: 20,

    }),
    control: () => ({
      // none of react-select's styles are passed to <Control />
      width: 200,
    }),
    singleValue: (provided, state) => {
      const opacity = state.isDisabled ? 0.5 : 1;
      const transition = 'opacity 300ms';


      return { ...provided, opacity, transition };
    }
  }
    return(

        <>
<div>

    <Select
    options={this.state.selectOptions}
    onChange={this.handleChange.bind(this)}
    styles={customStyles}

    />

    <p>
        Yout slect <strong>{this.state.stock_name}</strong>
    </p>
</div>

        </>
    );
}
}
export default AddStockMockup;
