            if(error.response.status==401)
            setError(error.response.data.message);

            else
            setError("Error")
        })
    }

    return (


        <>

<div class="stock-fill-form">

Stockname<br/>
<input type="text" {...stockname} />

Transaction Type:<br/>
<select value={transactiontype} >


