import React, { useEffect, useState } from 'react';

import "../StockDashboard/StockDashboard.css"
import Sidenav from '../Widget/Stocklist/Sidenav/SideNav';
function StockDashBoard(){


  const[ stockname, setStockname]=useState([])

  const [allstock,setAllStock]=useState();
  const [totalbuy, setTotalBuy]=useState([])
const table_header=["Company Name", "Total Units"]
//   const all=[...stockname, ...allstock]

// console.log(all)
//   console.log(typeof(allstock))



  useEffect(()=>{

    fetch("http://localhost:8000/all-stocks")
    .then(res=>res.json())
    .then(
        (result)=>{

            // setStockname(result)
            // console.log(result)

            const allstock=result.map(x=> x.stockname)
            const unique=[...new Set(allstock)]

            // setAllStock(unique)
          setStockname(unique)








          const count=[];
            for (var i=0; i<=unique.length-1; i++){

                // const total=result.filter((item)=>item.stockname==unique[i]).map((item=>parseInt(item.amount)));
                // const totalbuy=total.reduce((a,b)=>a+b)

                // setTotalBuy(totalbuy)



                const totalbuy= result.filter((item)=> item.stockname==unique[i]).map((item)=>item.amount)

                 const total=totalbuy.reduce((a,b)=>parseInt(a)+parseInt(b));
                 console.log(total)
            count.push(parseInt(total))
            // console.log(count)


            // setAllStock(total)



            } 
            setAllStock(count)




        }


    )
  },[])



    return (


        <>
<div  className="stockdashboard-main">

<div className="stokdashboard-part1">
<Sidenav/>
</div>

<div className="stokdashboard-part2">

<table className="table">



    <tr className="tr">
    {

        table_header.map(item=>(

            <td className="td">
                {item}

                </td>
        ))
    }
</tr>


<tr className="tr">
{
   stockname && stockname.map(item=>(


            <td className="td">
            {item}
                </td>


    ))
}
</tr>

<tr className="tr">
{
   allstock && allstock.map(item=>(


    <td className="td">
            {item}
            </td>

    ))
}
</tr>




</table>
</div>

</div>
        </>
    );
}

export default StockDashBoard;
