import React, { useEffect, useState } from 'react';
import Sidenav from '../Widget/Stocklist/Sidenav/SideNav';
import {getUser} from "../utils/Common";
import "../DashBoard/Dashboard.css"
import { reduceHooks } from 'react-table';


function DashBoard(){

    const [error, setError]=useState(null);

    const [stocks,setStocks]=useState([]);
    const [total,setTotal]=useState("");

    const user=getUser();




    useEffect(()=>{

        fetch(`http://localhost:8000/get-all?user=${user}`)
        .then(res=>res.json())
        .then(
            (result)=>{

                setStocks(result);
               const sum=result.map(item=>item.quantity).reduce((a,b)=>a+b);
               setTotal(sum);
               console.log(sum)
            },
            (error)=>{
                setError(error);


            }
        )
    },[])
        return (

            <>
    <div className="dashboard-main">
    <div className="dashboard-sidenav">
    <Sidenav/>

    </div>

        <div className="dashboard-part1">


        </div>

        <div className="dashboard-part2">

        Total:{total}

{/*   
    {
    stocks.map(item=>( 

         <div>{item.id}</div>


    ))
    }
     */}
        </div>     
    </div>

            </>
        );
    }




    export default DashBoard;
